def any2set(obj: AnyQ, hush: bool = True) -> set:
    '''Tries to convert any object to a set. If the object is a string, it will be converted to a set of length 1.'''
    if isinstance(obj, str):        
        obj = set((obj, ))

    elif not isiter(obj):
        # not a string and not iterable
        obj = set((obj, ))

    elif isinstance(obj, (list, tuple, Iterable)) and not isset(obj, set):
        obj = set(obj)

    elif hasattr(obj, '__hash__'):
        obj = {obj, }

    else:
        try:
            obj = set(obj)
        except TypeError:
            if not hush:
                raise TypeError(f'Could not convert {obj} to set.')
            obj = set()

    return typenone(obj, set)

